class PostComments{constructor(e){this.postId=e,this.postContainer=$(`#post-${e}`),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(n){n.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(n){console.log("you are in comment create NOTY!!");let o=t.newCommentDom(n.data.comment);$(`#post-comments-${e}`).prepend(o),t.deleteComment($(" .delete-comment-button",o)),new ToggleLike($(" .toggle-like-button",o)),new Noty({theme:"relax",text:"Comment published!!!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}newCommentDom(e){return $(`<li class="each-comment" id="comment-${e._id}">\n                    <p class="each-comment-text">\n                        \n                            <small class="small-delete">\n                                <a class="delete-comment-button" href="/comments/destroy/${e._id}">X</a>\n                            </small>\n                        \n                \n                        <div class="comment-user">\n                            \n                                <img src="${e.user.avatar}" alt="${e.user.name}" width="100">\n                            \n                            <p class="comment-user-name"> ${e.user.name} </p>\n                            <p class="comment-timing">${e.createdAt}</p>\n                            \n                            <small>\n                                \n                                    <a class="toggle-like-button" data-likes="${e.likes.length}" href="/likes/toggle/?id=${e._id}&type=Comment">\n                                        <i class="fa-regular fa-heart"></i> ${e.likes.length} \n                                    </a>\n                            \n                            </small>\n                        </div>\n                \n                        <p class="comment-content">${e.content}</p>\n\n                    </p> \n                </li>`)}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$(`#comment-${e.data.comment_id}`).remove(),new Noty({theme:"relax",text:"Comment Deleted !!!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}}